package com.statments;
//predict the output
public class NestedForLoopCode2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int term=6;
     for(int i=0;i<=term;i++) {
    	 for(int j=term;j>=i;j--) {
    		 System.out.println("*");
    	 }
     }
	}
}
